# Hàm để lưu lại các câu hỏi không thể trả lời
def write_to_fallback_file(filename: Path, message: str):
    """Ghi câu hỏi vào file fallback với định dạng thống nhất"""
    try:
        # Tạo thư mục nếu chưa tồn tại
        filename.parent.mkdir(parents=True, exist_ok=True)

        # Thêm timestamp và định dạng câu hỏi
        timestamp = datetime.now().strftime("%Y-%m-%d %H:%M:%S")
        formatted_message = f" - [{timestamp}] {message.strip()}\n"

        with open(filename, "a", encoding="utf-8") as f:
            f.write(formatted_message)

        # Trả về đường dẫn file để hiển thị cho người dùng
        return str(filename)
    except Exception as e:
        print(f"Error writing to fallback file: {e}")
        return None


