def remove_duplicates(entities):
    """Loại bỏ các entity trùng nhưng vẫn giữ nguyên thứ tự."""
    if entities is None:
        return None
    return list(OrderedDict.fromkeys(entities))


# Hàm chung để cung cấp hướng dẫn và link video hướng dẫn
class ActionProvideGuide(Action):
    def name(self) -> Text:
        return "action_provide_guide"

    def run(self, dispatcher: CollectingDispatcher, tracker: Tracker, domain: Dict[Text, Any]) -> List[Dict[Text, Any]]:

        user_message = tracker.latest_message.get("text", "")

        # Lấy entity từ slot
        hoat_dong_chinh = tracker.get_slot("hoat_dong_chinh")
        hoat_dong_phu = tracker.get_slot("hoat_dong_phu")

        doi_tuong = tracker.get_slot("doi_tuong")
        doi_tuong_phu = tracker.get_slot("doi_tuong_phu")

        trang_thai = tracker.get_slot("trang_thai")

        tro_tu = tracker.get_slot("tro_tu")
        tro_tu2 = tracker.get_slot("tro_tu2")

        # Lọc trùng mà vẫn giữ thứ tự xuất hiện
        hoat_dong_chinh = remove_duplicates([hoat_dong_chinh]) if hoat_dong_chinh else []
        hoat_dong_phu = remove_duplicates([hoat_dong_phu]) if hoat_dong_phu else []

        doi_tuong = remove_duplicates([doi_tuong]) if doi_tuong else []
        doi_tuong_phu = remove_duplicates([doi_tuong_phu]) if doi_tuong_phu else []

        trang_thai = remove_duplicates([trang_thai]) if trang_thai else []

        tro_tu = remove_duplicates([tro_tu]) if tro_tu else []
        tro_tu2 = remove_duplicates([tro_tu2]) if tro_tu2 else []

        # Chắc chắn rằng doi_tuong và doi_tuong_phu không phải là danh sách rỗng
        doi_tuong = doi_tuong[0] if isinstance(doi_tuong, list) and doi_tuong else doi_tuong
        doi_tuong_phu2 = doi_tuong_phu
        doi_tuong_phu = doi_tuong_phu[0] if isinstance(doi_tuong_phu, list) and doi_tuong_phu else doi_tuong_phu

        # Kiểm tra xem có doi_tuong hợp lệ không
        node = None
        if doi_tuong and isinstance(doi_tuong, str) and doi_tuong in ACTIONS_DATA:
            node = ACTIONS_DATA[doi_tuong]
        elif doi_tuong_phu and isinstance(doi_tuong_phu, str) and doi_tuong_phu in ACTIONS_DATA:
            node = ACTIONS_DATA[doi_tuong_phu]
            doi_tuong = doi_tuong_phu  # Cập nhật doi_tuong nếu ban đầu không có
        elif hoat_dong_chinh and "tải" in hoat_dong_chinh or "sử dụng" in hoat_dong_chinh and not doi_tuong and not doi_tuong_phu:
            node = ACTIONS_DATA["phần mềm"]
        elif tro_tu and "dây" in tro_tu and not doi_tuong and not doi_tuong_phu:
            node = ACTIONS_DATA["hụi"]

        if not node:
            # Ghi câu hỏi vào file Fallback2.txt khi không tìm thấy node phù hợp
            file_path = write_to_fallback_file(FALLBACK2_FILE, user_message)

            dispatcher.utter_message(text="Xin lỗi, Chatbot chưa hiểu ý của Anh/Chị.")

        # Xác định hoat_dong_chinh
        if hoat_dong_chinh and hoat_dong_chinh[0] in node:
            node = node[hoat_dong_chinh[0]]
        elif hoat_dong_phu and hoat_dong_phu[0] in node:
            node = node[hoat_dong_phu[0]]
        elif trang_thai and "mãn" in trang_thai and not hoat_dong_chinh and not hoat_dong_phu:
            node = node.get("mãn", node)

        # Tiếp tục xử lý các trường hợp khác (tro_tu, tro_tu2, trang_thai)
        if "sub" in node:
            if "sub" in node and doi_tuong_phu2 and doi_tuong_phu2[0] in node["sub"]:
                node = node["sub"][doi_tuong_phu2[0]]
            if "sub" in node and hoat_dong_phu and hoat_dong_phu[0] in node["sub"]:
                node = node["sub"][hoat_dong_phu[0]]
            if "sub" in node and tro_tu and tro_tu[0] in node["sub"]:
                node = node["sub"][tro_tu[0]]
            if "sub" in node and tro_tu2 and tro_tu2[0] in node["sub"]:
                node = node["sub"][tro_tu2[0]]
            if "sub" in node and trang_thai and trang_thai[0] in node["sub"]:
                node = node["sub"][trang_thai[0]]

        # Nếu đến đây mà `node` có chứa thông tin, trả về kết quả
        if isinstance(node, dict) and "name" in node and "link" in node:
            if node['name'] == "tải phần mềm":
                message = f"{node['details']}"
                dispatcher.utter_message(text=message)

            elif node['name'] == "sử dụng phần mềm":
                video_link = node['link']
                message = (f"{node['details']} <br><br> <a href='{video_link}' target='_blank'>Kênh Youtube hướng dẫn</a><hr><br>"
                           f"<img src='{node['image']}' class='chatbot-image'>")
                dispatcher.utter_message(text=message)

            else:
                video_link = node['link']
                message = (f"Để <b>{node['name']}</b>, Anh/Chị hãy làm theo các bước hướng dẫn sau:<br><hr>"
                           f"{node['details']}<br><br>"
                           f"Chi tiết Anh/Chị có thể xem hình bên dưới: <br><hr>"
                           f"<img src='{node['image']}' class='chatbot-image'><br><hr>"
                           f"Hoặc Anh/Chị có thể xem video hướng dẫn sau thì sẽ dễ hiểu hơn<br> {video_link}")
                dispatcher.utter_message(text=message)

        else:
            # Ghi câu hỏi vào file Fallback2.txt khi không tìm thấy thông tin phù hợp trong node
            file_path = write_to_fallback_file(FALLBACK2_FILE, user_message)

            dispatcher.utter_message(
                text="Xin lỗi, em chưa hiểu ý của Anh/Chị.<br>"
                     "Anh/Chị hãy hỏi gắn gọn giúp em, ví dụ như <b>Hốt hụi, đóng hụi, tạo dây hụi, ...</b>"
                     "Nếu vẫn không Chatbot không thể trả lời, Anh/Chị hãy nhắn hỏi bên nhóm Zalo sau giúp Chatbot nhé: <a href='https://zalo.me/g/vnrlgm076' target='_blank'>Nhóm Zalo</a>")

        # Sau khi trả lời, đặt lại tất cả các biến thành rỗng
        hoat_dong_chinh = []
        hoat_dong_phu = []
        doi_tuong = None
        doi_tuong_phu = None
        trang_thai = []
        tro_tu = []
        tro_tu2 = []
        node = None
        # 🔹 Reset slots sau khi gửi tin nhắn
        return [
            SlotSet("hoat_dong_chinh", None),
            SlotSet("hoat_dong_phu", None),
            SlotSet("doi_tuong", None),
            SlotSet("doi_tuong_phu", None),
            SlotSet("trang_thai", None),
            SlotSet("tro_tu", None),
            SlotSet("tro_tu2", None)
        ]


